import React, { useState } from "react";
import axios from "axios";
import CalculatedData from "../main/CalculatedData";
import Cookies from "js-cookie";

export default function Add({ isAuthenticated }) {
  const [operandOne, setOperandOne] = React.useState(0);
  const [operandTwo, setOperandTwo] = React.useState(0);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = React.useState(0);
  const [newData, setNewData] = useState(true);

  const fetchResult = e => {
    e.preventDefault();

    const storedToken = Cookies.get("token");
    const params = {
      operandOne: operandOne,
      operandTwo: operandTwo
    };
    setLoading(true);
    axios
      .post(
        `${process.env.REACT_APP_BACKEND_URL}/api/calculation/add`,
        params,
        {
          headers: {
            token: storedToken
          }
        }
      )
      .then(res => {
        setResult(res.data.result);
        setNewData(false);
      })
      .then(() => setLoading(false));
  };

  return (
    <>
      <h1>Addition</h1>
      <TwoFieldInput
        operandOne={operandOne}
        operandTwo={operandTwo}
        updateOperandOne={e => {
          if (!newData) setNewData(true);
          setOperandOne(e.target.value);
        }}
        updateOperandTwo={e => {
          if (!newData) setNewData(true);
          setOperandTwo(e.target.value);
        }}
      />
      <button className="btn btn-primary" onClick={e => fetchResult(e)}>
        Add
      </button>
      {!loading && !newData && (
        <div>
          The sum of {operandOne} and {operandTwo} is {result}
        </div>
      )}
      {!loading && <CalculatedData />}
    </>
  );
}
