import React, { useState } from "react";
import axios from "axios";
import CalculatedData from "../main/CalculatedData";
import Cookies from "js-cookie";

export default function Add({ isAuthenticated }) {
  const [operandOne, setOperandOne] = React.useState(0);
  const [operandTwo, setOperandTwo] = React.useState(0);
  const [loading, setLoading] = useState(true);
  const [result, setResult] = React.useState(0);

  const fetchResult = e => {
    e.preventDefault();

    const storedToken = Cookies.get("token");
    const params = {
      operandOne: operandOne,
      operandTwo: operandTwo
    };
    axios
      .post("http://localhost:8000/api/calculation/add", params, {
        headers: {
          token: storedToken
        }
      })
      .then(res => {
        setResult(res.data.result);
      })
      .then(() => setLoading(false));
  };

  return (
    <>
      <h1>Addition</h1>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center"
        }}
      >
        <label>First Number:</label>
        <input
          style={{ margin: "15px", width: "25%" }}
          className="form-control"
          value={operandOne}
          onChange={e => {
            setOperandOne(e.target.value);
          }}
        />
        <label>Second Number:</label>
        <input
          style={{ margin: "15px", width: "25%" }}
          className="form-control"
          value={operandTwo}
          onChange={e => {
            setOperandTwo(e.target.value);
          }}
        />
      </div>
      <button className="btn btn-primary" onClick={e => fetchResult(e)}>
        Add
      </button>
      {!loading && (
        <div>
          The sum of {operandOne} and {operandTwo} is {result}
        </div>
      )}
      <CalculatedData />
    </>
  );
}
